import React, { useState, useEffect } from 'react';
import CalendarComponent from './calendarComponent';
import LineChart from './lineChart.js';

const EnergyConsumption = () => {
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedDevice, setSelectedDevice] = useState(null);
    const [deviceIds, setDeviceIds] = useState([]);
    const [energyData, setEnergyData] = useState([]);

    useEffect(() => {
        // Fetch device IDs when the component mounts
        const fetchDeviceIds = async () => {
            try {
                const response = await fetch('http://localhost:7070/getDeviceIds');
                const data = await response.json();
                const ids = data.map(device => device.deviceID); // Update to use deviceID
                setDeviceIds(ids);
                setSelectedDevice(ids.length > 0 ? ids[0] : null);
            } catch (error) {
                console.error('Error fetching device IDs:', error);
            }
        };

        fetchDeviceIds();
    }, []); // Empty dependency array ensures the effect runs only once on mount

    const handleDateSelect = (date) => {
        setSelectedDate(date);
    };

    const handleDeviceSelect = (event) => {
        setSelectedDevice(event.target.value);
    };

    const handleGenerateChart = async () => {
        try {
            if (!selectedDate) {
                console.error('Selected date is null.');
                return;
            }

            // Assuming selectedDevice is the device ID
            const formattedDate = selectedDate.toISOString().split('T')[0];
            const response = await fetch(`http://localhost:7070/daily/${selectedDevice}/${formattedDate}`);
            const data = await response.json();
            setEnergyData(data);
        } catch (error) {
            console.error('Error fetching energy consumption data:', error);
        }
    };



    return (
        <div>
            <h2>Energy Consumption Chart</h2>

            <div>
                <label>Select Date: </label>
                <CalendarComponent onDateSelect={handleDateSelect} />
            </div>

            <div>
                <label>Select Device: </label>
                <select onChange={handleDeviceSelect} value={selectedDevice}>
                    {deviceIds.map((deviceId, index) => (
                        <option key={index} value={deviceId}>
                            {deviceId}
                        </option>
                    ))}
                </select>
            </div>

            <button onClick={handleGenerateChart}>Generate Chart</button>

            {selectedDate && selectedDevice && (
                <div>
                    <h3>Selected Date: {selectedDate.toDateString()}</h3>
                    <h3>Selected Device ID: {selectedDevice}</h3>
                    <LineChart data={energyData} />
                </div>
            )}
        </div>
    );
};

export default EnergyConsumption;
