import React, { useState, useEffect } from 'react';
import Stomp from 'webstomp-client';

const ChatComponent = () => {
    const [stompClient, setStompClient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    useEffect(() => {
        const socket = new WebSocket(process.env.NODE_ENV === 'development' ? 'ws://localhost:8080/chat' : 'ws://localhost:8080/chat');

        socket.onopen = () => {
            console.log('WebSocket connection opened.');
        };

        socket.onclose = (event) => {
            console.error(`WebSocket closed: ${event.reason} (code ${event.code})`);
        };

        socket.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        const stomp = Stomp.over(socket);

        stomp.connect({}, () => {
            setStompClient(stomp);
            console.log('Connected to WebSocket');
            stomp.subscribe('/topic/messages', onMessage);
        });

        return () => {
            if (stomp.connected) {
                stomp.disconnect();
                console.log('Disconnected from WebSocket');
            }
        };
    }, []);

    const onMessage = (msg) => {
        const newMessage = JSON.parse(msg.body);
        setMessages([...messages, newMessage]);
    };

    const sendMessage = () => {
        if (stompClient && inputMessage.trim() !== '') {
            const message = {
                from: 'client',
                message: inputMessage,
            };

            stompClient.send('/app/chat', JSON.stringify(message));

            setInputMessage('');
        }
    };

    return (
        <div>
            <h2>Chat</h2>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.from}:</strong> {msg.message}
                    </div>
                ))}
            </div>
            <div>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                />
                <button onClick={sendMessage} disabled={!stompClient?.connected}>
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatComponent;
