import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { getUserByUsername } from "../person/api/person-api";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            secretKey: '', // Initialize secretKey
        };
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const { username, password } = this.state;

        const loginURL = `http://localhost:8080/login/${username}/${password}`;

        // Initialize secretKey
        this.setState({ secretKey: 'initial-secret-key' });

        fetch(loginURL, {
            method: 'GET',
        })
            .then(response => {
                const secretKey = response.headers.get('Secret-Key');

                if (response.ok) {
                    return response.json(); // Parse response as JSON
                } else {
                    throw new Error('Authentication failed');
                }
            })
            .then(data => {
                const { token, role, error } = data;

                // Update secretKey in the state
                this.setState({ secretKey }, () => {
                    sessionStorage.setItem('secretKey', this.state.secretKey);
                    console.log('Received secretKey:', this.state.secretKey);
                });

                if (error) {
                    alert('Authentication failed: ' + error);
                    return;
                }

                sessionStorage.setItem('userRole', role);
                sessionStorage.setItem('userToken', token);

                if (role === 'admin') {
                    alert('Login successful: ' + role);
                    this.props.history.push('/admin/home');
                } else if (role === 'client') {
                    alert('Login successful: ' + role);
                    this.fetchUserDevicesAndRedirect(username);
                } else {
                    alert('Invalid user role: ' + role);
                }
            })
            .catch(error => {
                console.error('Network error:', error);
            });
    }

    fetchUserDevicesAndRedirect = (username) => {
        getUserByUsername(username, (userId) => {
            console.log('Fetched userId:', userId);

            this.setState({ userId });
            const devicesURL = `http://localhost:9090/getDevices`;

            fetch(devicesURL, {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + sessionStorage.getItem('userToken'),
                    'Secret-Key': this.state.secretKey, // Add this line
                },
            })
                .then(response => {
                    if (response.status === 200) {
                        return response.json();
                    } else {
                        throw Error('Failed to fetch user devices');
                    }
                })
                .then(devices => {
                    this.props.history.push({
                        pathname: '/client/device',
                        state: { devices, userId },
                    });

                })
                .catch(error => {
                    console.error('Failed to fetch user devices:', error);
                });
        });
    }

    render() {
        return (
            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={this.handleSubmit} className="login-form">
                    <input
                        type="text"
                        name="username"
                        value={this.state.username}
                        onChange={this.handleInputChange}
                        placeholder="Username"
                        className="login-input"
                    />
                    <input
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.handleInputChange}
                        placeholder="Password"
                        className="login-input"
                    />
                    <button type="submit" className="login-button">Login</button>
                </form>
            </div>
        );
    }
}

export default withRouter(Login);
