import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import './style/login.css';
import { getUserByUsername } from "../person/api/person-api";
import jwtDecode from 'jwt-decode'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
        };
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const { username, password } = this.state;

        const loginURL = `http://localhost:8080/login/${username}/${password}`;

        fetch(loginURL, {
            method: 'GET',
        })
            .then(response => {
                if (response.status === 200) {
                    return response.text();
                } else {
                    throw new Error('Authentication failed');
                }
            })
            .then(token => {
                // Store the token in localStorage
                localStorage.setItem('token', token);

                // Decode the token to get user's role
                const decodedToken = jwt_decode(token);
                const role = decodedToken.role;

                if (role === 'admin') {
                    sessionStorage.setItem('userRole', role);
                    alert('Login successful: ' + role);
                    this.props.history.push('/admin/home');
                } else if (role === 'client') {
                    sessionStorage.setItem('userRole', role);
                    alert('Login successful: ' + role);
                    this.fetchUserDevicesAndRedirect(username);
                } else {
                    alert('Invalid user role: ' + role);
                }
            })
            .catch(error => {
                console.error('Network error:', error);
            });
    }

    fetchUserDevicesAndRedirect = (username) => {
        getUserByUsername(username, (userId) => {
            console.log('Fetched userId:', userId);

            this.setState({ userId });
            const devicesURL = `http://localhost:9090/getDevices`;

            const token = localStorage.getItem('token');

            fetch(devicesURL, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
            })
                .then(response => {
                    if (response.status === 200) {
                        return response.json();
                    } else {
                        throw Error('Failed to fetch user devices');
                    }
                })
                .then(devices => {
                    this.props.history.push({
                        pathname: '/client/device',
                        state: { devices, userId },
                    });
                })
                .catch(error => {
                    console.error('Failed to fetch user devices:', error);
                });
        });
    }

    render() {
        return (
            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={this.handleSubmit} className="login-form">
                    <input
                        type="text"
                        name="username"
                        value={this.state.username}
                        onChange={this.handleInputChange}
                        placeholder="Username"
                        className="login-input"
                    />
                    <input
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.handleInputChange}
                        placeholder="Password"
                        className="login-input"
                    />
                    <button type="submit" className="login-button">Login</button>
                </form>
            </div>
        );
    }
}

export default withRouter(Login);
