// ChatComponent.js
import React, { useState } from 'react';
import Stomp from 'react-stomp';

const ChatComponent = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    const onMessage = (msg) => {
        const newMessage = JSON.parse(msg.body);
        setMessages([...messages, newMessage]);
    };

    const sendMessage = (stompClient) => {
        if (inputMessage.trim() !== '') {
            const message = {
                from: 'client', // Update with the user's role or ID
                message: inputMessage,
            };

            // Send the message to the server
            stompClient.sendMessage('/app/chat/sendMessage', JSON.stringify(message));

            // Clear the input field
            setInputMessage('');
        }
    };

    return (
        <div>
            <h2>Chat</h2>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.from}:</strong> {msg.message}
                    </div>
                ))}
            </div>
            <div>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                />
                <Stomp
                    url="ws://localhost:8080/chat"
                    topics={['/topic/user']}
                    onConnect={(stompClient) => sendMessage(stompClient)}
                    onMessage={onMessage}  // Use the onMessage function here
                    debug={false}
                />
            </div>
        </div>
    );
};

export default ChatComponent;
