import React, { useState } from 'react';
import { Stomp } from 'react-stomp';
// Add any additional styles or import statements

const ChatComponent = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    const onMessage = (msg) => {
        const newMessage = JSON.parse(msg.body);
        setMessages([...messages, newMessage]);
    };

    const sendMessage = (stompClient) => {
        if (stompClient && inputMessage.trim() !== '') {
            const message = {
                from: 'client',
                message: inputMessage,
            };

            stompClient.send('/app/chat/sendMessage', JSON.stringify(message));

            setInputMessage('');
        }
    };

    return (
        <div>
            <h2>Chat</h2>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.from}:</strong> {msg.message}
                    </div>
                ))}
            </div>
            <div>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                />
                <Stomp
                    url="ws://localhost:1010/chat"
                    topics={['/topic/user', '/topic/admin']}
                    onConnect={(stompClient) => console.log('Connected to WebSocket')}
                    onMessage={onMessage}
                >
                    {(stompClient) => (
                        <button onClick={() => sendMessage(stompClient)} disabled={!stompClient.connected}>
                            Send
                        </button>
                    )}
                </Stomp>
            </div>
        </div>
    );
};

export default ChatComponent;
