import { HOST } from '../../commons/hosts';
import RestApiClient from "../../commons/api/rest-client";

const endpoint = {
    device: '/getDevices',
    deleteDevice: '/deleteDevice',
    createDevice: '/createDevice',
    editDevice: '/updateDevice'
};

function getDevices(token, secretKey) {
    const url = HOST.backend_devices + endpoint.device;

    console.log('Fetching devices with secretKey:', secretKey);

    return fetch(url, {
        method: 'GET',
        headers: {
            'Authorization': 'Bearer ' + token,
            'Secret-Key': secretKey,
            'Content-Type': 'application/json',
        },
    })
        .then(response => {
            console.log('Response status:', response.status);

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            if (response.headers.get('content-type') && response.headers.get('content-type').includes('application/json')) {
                return response.json();
            } else {
                return [];
            }
        })
        .catch(error => {
            console.error('Error fetching devices:', error);
            throw error; // Rethrow the error to propagate it to the caller
        });
}

function createDevice(device, secretKey) {
    const token = sessionStorage.getItem('token');
    const url = HOST.backend_devices + endpoint.createDevice;

    return fetch(url, {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Secret-Key': secretKey,
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(device),
    })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            return response.json();
        })
        .catch(error => {
            console.error('Error creating device:', error);
            throw error;
        });
}

function deleteDevice(id, secretKey) {
    const token = sessionStorage.getItem('token');
    const url = HOST.backend_devices + endpoint.deleteDevice + '/' + id;

    return fetch(url, {
        method: 'DELETE',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Secret-Key': secretKey,
        },
    })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            return response.json();
        })
        .catch(error => {
            console.error('Error deleting device:', error);
            throw error;
        });
}

function editDevice(id, device, secretKey) {
    const token = sessionStorage.getItem('token');
    const url = HOST.backend_devices + endpoint.editDevice + `/` + id;

    return fetch(url, {
        method: 'PATCH',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Secret-Key': secretKey,
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(device),
    })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            return response.json();
        })
        .catch(error => {
            console.error('Error editing device:', error);
            throw error;
        });
}

export {
    getDevices,
    createDevice,
    deleteDevice,
    editDevice
};
