import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { getUserByUsername } from "../person/api/person-api";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
        };
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const { username, password } = this.state;

        const loginURL = `http://localhost:8080/login/${username}/${password}`;

        fetch(loginURL, {
            method: 'GET',
        })
            .then(response => {
                if (response.ok) {
                    return response.json(); // Parse response as JSON
                } else {
                    throw new Error('Authentication failed');
                }
            })
            .then(data => {
                const { token, role, secretKey, error } = data;

                if (error) {
                    alert('Authentication failed: ' + error);
                    return;
                }

                sessionStorage.setItem('userRole', role);
                sessionStorage.setItem('userToken', token);
                sessionStorage.setItem('secretKey', secretKey);

                if (role === 'admin') {
                    alert('Login successful: ' + role);
                    this.props.history.push('/admin/home');
                } else if (role === 'client') {
                    alert('Login successful: ' + role);
                    this.fetchUserDevicesAndRedirect(username, secretKey);
                } else {
                    alert('Invalid user role: ' + role);
                }
            })
            .catch(error => {
                console.error('Network error:', error);
            });
    }

    // fetchUserDevicesAndRedirect = (username, secretKey) => {
