import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import NavigationBar from './navigation-bar';
import NavigationBar2 from './navigation-bar2';
import Home from './home/home';
import PersonContainer from './person/person-container';
import ErrorPage from './commons/errorhandling/error-page';
import styles from './commons/styles/project-style.css';
import Login from './login/login';
import DeviceContainer from './device/device-container';
import Home1 from './home/home1';
import UserDeviceContainer from './userDevices/userDevice-container';
import ProtectedRoute from "./protected-route";


class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false,
        };
    }

    render() {
        const userRole = sessionStorage.getItem('userRole'); // Get the user's role from session storage

        return (
            <div className={styles.back}>
                <Router>
                    <div>
                        <Switch>
                            <Route
                                exact
                                path='/'
                                render={() => <Login/>}
                            />

                            {/* Render NavigationBar for admin and NavigationBar2 for client */}
                            {userRole === 'admin' && (
                                <Route
                                    path="/admin"
                                    render={() => <NavigationBar/>}
                                />
                            )}
                            {userRole === 'client' && (
                                <Route
                                    path="/client"
                                    render={() => <NavigationBar2/>}
                                />
                            )}

                            <ProtectedRoute exact path="/admin/home" component={Home} requiredRole="admin"/>
                            <ProtectedRoute exact path="/admin/device" component={DeviceContainer}
                                            requiredRole="admin"/>
                            <ProtectedRoute exact path="/admin/users" component={PersonContainer} requiredRole="admin"/>
                            <ProtectedRoute exact path="/admin/userdevices" component={UserDeviceContainer}
                                            requiredRole="admin"/>
                            <ProtectedRoute exact path="/client/home1" component={Home1} requiredRole="client"/>

                            <Route
                                exact
                                path='/person'
                                render={() => <PersonContainer/>}
                            />
                            <Route
                                exact
                                path='/error'
                                render={() => <ErrorPage/>}
                            />
                            <Route render={() => <ErrorPage/>}/>
                        </Switch>
                    </div>
                </Router>
            </div>
        );
    }

}
export default App;
