import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import NavigationBar from './NavigationBar';
import NavigationBar2 from './NavigationBar2';
import Home from './home/Home';
import PersonContainer from './person/PersonContainer';
import ErrorPage from './commons/errorhandling/ErrorPage';
import Login from './login/Login';
import DeviceContainer from './device/DeviceContainer';
import Home1 from './home/Home1';
import UserDeviceContainer from './userDevices/UserDeviceContainer';
import ProtectedRoute from './ProtectedRoute';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false,
        };
    }

    render() {
        const userRole = sessionStorage.getItem('userRole'); // Get the user's role from session storage

        return (
            <div className={styles.back}>
                <Router>
                    <div>
                        {userRole === 'admin' && (
                            <Route
                                path="/admin"
                                render={() => <NavigationBar />}
                            />
                        )}
                        {userRole === 'client' && (
                            <Route
                                path="/client"
                                render={() => <NavigationBar2 />}
                            />
                            )}
                        <Switch>
                            <Route
                                exact
                                path='/'
                                render={() => <Login />}
                            />

                            <ProtectedRoute
                                exact
                                path="/admin/home"
                                component={Home}
                                requiredRole="admin"
                            />
                            <ProtectedRoute
                                exact
                                path="/admin/device"
                                component={DeviceContainer}
                                requiredRole="admin"
                            />
                            <ProtectedRoute
                                exact
                                path="/admin/users"
                                component={PersonContainer}
                                requiredRole="admin"
                            />
                            <ProtectedRoute
                                exact
                                path="/admin/userdevices"
                                component={UserDeviceContainer}
                                requiredRole="admin"
                            />
                            <ProtectedRoute
                                exact
                                path="/client/home1"
                                component={Home1}
                                requiredRole="client"
                            />
                            <Route
                                exact
                                path='/person'
                                render={() => <PersonContainer />}
                            />
                            <Route
                                exact
                                path='/error'
                                render={() => <ErrorPage />}
                            />
                            <Route
                                render={() => <Redirect to="/error" />}
                            />
                        </Switch>
                    </div>
                </Router>
            </div>
        );
    }
}

export default App;
