// Import the necessary React and Bootstrap components
import React from 'react';
import {
    Button,
    Card,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalHeader,
    Row,
} from 'reactstrap';

// Import your device-related components and API functions
import DeviceForm from './components/device-form';
import * as API_DEVICES from './api/device-api';
import DeviceTable from './components/device-table';
import DeviceDeleteForm from './components/device-delete-form';
import DeviceEditForm from './components/device-edit-form';
import ChatComponent from "../ChatComponent";

// Import the ChatComponent for messaging

class DeviceContainer extends React.Component {
    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);
        this.toggleEditForm = this.toggleEditForm.bind(this);

        this.reload = this.reload.bind(this);
        this.state = {
            selected: false,
            collapseForm: false,
            tableData: [],
            isLoaded: false,
            errorStatus: 0,
            error: null,
            isDeleteFormVisible: false,
            isEditFormVisible: false,
            editingDeviceId: null,
            username: '',
            isChatVisible: false,  // State to manage the visibility of the chat
        };
    }

    componentDidMount() {
        this.fetchDevices();
    }

    fetchDevices() {
        return API_DEVICES.getDevices((result, status, err) => {
            if (result !== null && status === 200) {
                this.setState({
                    tableData: result,
                    isLoaded: true,
                });
            } else {
                this.setState({
                    errorStatus: status,
                    error: err,
                });
            }
        });
    }

    toggleForm() {
        this.setState({ selected: !this.state.selected });
    }

    toggleEditForm() {
        this.setState({
            isEditFormVisible: !this.state.isEditFormVisible,
        });
    }

    toggleDeleteDeviceForm = () => {
        this.setState({ isDeleteFormVisible: !this.state.isDeleteFormVisible });
    };

    toggleChat = () => {
        this.setState({ isChatVisible: !this.state.isChatVisible });
    };

    reload() {
        this.setState({
            isLoaded: false,
        });
        this.toggleForm();
        this.fetchDevices();
    }

    render() {
        return (
            <div>
                <CardHeader>
                    <strong> Devices Management </strong>
                </CardHeader>
                <Card>
                    <br />
                    <Row>
                        <Col sm={{ size: '8', offset: 1 }}>
                            {/* Add a button or icon to toggle the chat */}
                            <Button color="primary" onClick={this.toggleChat}>
                <span role="img" aria-label="message-icon">
                  📬
                </span>{' '}
                                Chat
                            </Button>{' '}
                            <Button color="primary" onClick={this.toggleForm}>
                                Add Device
                            </Button>{' '}
                            <Button color="danger" onClick={this.toggleDeleteDeviceForm}>
                                Delete Device
                            </Button>{' '}
                            <Button color="info" onClick={this.toggleEditForm}>
                                Edit Device
                            </Button>
                        </Col>
                    </Row>
                    <br />
                    <Row>
                        <Col sm={{ size: '9', offset: 1 }}>
                            {this.state.isLoaded && <DeviceTable tableData={this.state.tableData} />}
                            {this.state.errorStatus > 0 && (
                                <APIResponseErrorMessage
                                    errorStatus={this.state.errorStatus}
                                    error={this.state.error}
                                />
                            )}
                        </Col>
                    </Row>
                </Card>

                <Modal isOpen={this.state.selected} toggle={this.toggleForm} className={this.props.className} size="lg">
                    <ModalHeader toggle={this.toggleForm}>Add Device:</ModalHeader>
                    <ModalBody>
                        <DeviceForm reloadHandler={this.reload} />
                    </ModalBody>
                </Modal>

                <Modal
                    isOpen={this.state.isDeleteFormVisible}
                    toggle={this.toggleDeleteDeviceForm}
                    className={this.props.className}
                    size="lg"
                >
                    <ModalHeader toggle={this.toggleDeleteDeviceForm}>Delete Device:</ModalHeader>
                    <ModalBody>
                        <DeviceDeleteForm reloadHandler={this.reload} />
                    </ModalBody>
                </Modal>

                <Modal
                    isOpen={this.state.isEditFormVisible}
                    toggle={this.toggleEditForm}
                    className={this.props.className}
                    size="lg"
                >
                    <ModalHeader toggle={this.toggleEditForm}>Edit Device:</ModalHeader>
                    <ModalBody>
                        <DeviceEditForm deviceID={this.state.editingDeviceId} reloadHandler={this.reload} />
                    </ModalBody>
                </Modal>

                {/* Render the ChatComponent based on the chat visibility state */}
                {this.state.isChatVisible && <ChatComponent />}
            </div>
        );
    }
}

export default DeviceContainer;
