import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import NavigationBar from './navigation-bar';
import NavigationBar2 from './navigation-bar2';
import Home from './home/home';
import PersonContainer from './person/person-container';
import ErrorPage from './commons/errorhandling/error-page';
import styles from './commons/styles/project-style.css';
import Login from './login/login';
import DeviceContainer from './device/device-container';
import Home1 from './home/home1';
import UserDeviceContainer from './userDevices/userDevice-container';
import MyDeviceContainer from './mydevices/mydevice-container';
import ProtectedRoute from './ProtectedRoute';
import Admin from './admin/Admin'; // Import the Admin component
import Client from './client/Client'; // Import the Client component

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false,
        };
    }

    render() {
        return (
            <div className={styles.back}>
                <Router>
                    <div>
                        <Switch>
                            <Route
                                exact
                                path='/'
                                render={() => <Login />}
                            />

                            {/* Use ProtectedRoute to protect admin and client routes */}
                            <ProtectedRoute exact path="/admin" component={Admin} requiredRole="admin" />
                            <ProtectedRoute exact path="/client" component={Client} requiredRole="client" />

                            <Route
                                exact
                                path='/home'
                                render={() => (
                                    <div>
                                        <NavigationBar />
                                        <Home />
                                    </div>
                                )}
                            />
                            <Route
                                exact
                                path='/home1'
                                render={() => (
                                    <div>
                                        <NavigationBar2 />
                                        <Home1 />
                                    </div>
                                )}
                            />
                            <Route
                                exact
                                path='/person'
                                render={() => <PersonContainer />}
                            />
                            <Route
                                exact
                                path='/userDevices'
                                render={() => <UserDeviceContainer />}
                            />
                            <Route
                                exact
                                path='/device'
                                render={() => <DeviceContainer />}
                            />
                            <Route
                                exact
                                path="/mydevices"
                                render={() =>  <MyDeviceContainer/>}
                            />
                            <Route
                                exact
                                path='/error'
                                render={() => <ErrorPage />}
                            />
                            <Route render={() => <ErrorPage />} />
                        </Switch>
                    </div>
                </Router>
            </div>
        );
    }
}

export default App;
