import { HOST } from '../../commons/hosts';
import RestApiClient from "../../commons/api/rest-client";

const endpoint = {
    device: '/getDevices',
    deleteDevice: '/deleteDevice',
    createDevice: '/createDevice',
    editDevice: '/updateDevice'
};


// ... Existing code ...

function getDevices(token) {
    const url = HOST.backend_devices + endpoint.device;

    console.log('Fetching devices with token:', token);

    return fetch(url, {
        method: 'GET',
        headers: {
            'Authorization': 'Bearer ' + token,
            'Content-Type': 'application/json',
        },
    })
        .then(response => {
            console.log('Response status:', response.status);

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Check if response body is not empty
            if (response.status !== 204) {
                return response.json();
            } else {
                return [];
            }
        })
        .catch(error => {
            console.error('Error fetching devices:', error);
            throw error; // Rethrow the error to propagate it to the caller
        });
}


function getDeviceById(params, callback) {
    const token = sessionStorage.getItem('token');
    let request = new Request(HOST.backend_devices + endpoint.device + params.id, {
        method: 'GET',
        headers: {
            'Authorization': `Bearer ${token}`,
        },
    });
    RestApiClient.performRequest(request, callback);
}

function postDevice(user, callback) {
    const token = sessionStorage.getItem('token');
    let request = new Request(HOST.backend_devices + endpoint.createDevice, {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(user)
    });
    RestApiClient.performRequest(request, callback);
}

function deleteDevice(id, token, callback) {
    let request = new Request(HOST.backend_devices + endpoint.deleteDevice + '/' + id, {
        method: 'DELETE',
        headers: {
            'Authorization': `Bearer ${token}`,
        },
    });

    console.log("URL: " + request.url);

    RestApiClient.performRequest(request, callback);
}

function editDevice(id, device, token, callback) {
    let request = new Request(HOST.backend_devices + endpoint.editDevice + `/` + id, {
        method: 'PATCH',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(device),
    });
    RestApiClient.performRequest(request, callback);
}


export {
    getDevices,
    getDeviceById,
    postDevice,
    deleteDevice,
    editDevice
};
