import React, { useState, useEffect } from 'react';
import * as Stomp from 'webstomp-client';
//import './chat-styles.css';

const ChatComponent = () => {
    const [stompClient, setStompClient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    const connectWebSocket = () => {
        const socket = new WebSocket(
            process.env.NODE_ENV === 'development'
                ? 'ws://localhost:1010/chat'  // Update the port if necessary
                : 'wss://localhost:1010/chat'
        );
        const stomp = Stomp.over(socket);

        stomp.connect({}, () => {
            setStompClient(stomp);
            console.log('Connected to WebSocket');

            // Subscribe to the appropriate topics based on user role
            const userRole = 'admin'; // Set the user role dynamically or fetch it from your authentication system
            const messageTopic = `/topic/${userRole === 'admin' ? 'user' : 'admin'}`;
            const typingTopic = `/topic/${userRole === 'admin' ? 'userTyping' : 'adminTyping'}`;
            const seenTopic = `/topic/${userRole === 'admin' ? 'userSeen' : 'adminSeen'}`;

            stomp.subscribe(messageTopic, onMessage);
            stomp.subscribe(typingTopic, onTyping);
            stomp.subscribe(seenTopic, onMessageSeen);
        }, (error) => {
            console.error('WebSocket connection error:', error);
            // Retry connecting after a delay
            setTimeout(connectWebSocket, 5000);
        });
    };

    useEffect(() => {
        connectWebSocket();

        return () => {
            if (stompClient && stompClient.connected) {
                stompClient.disconnect();
                console.log('Disconnected from WebSocket');
            }
        };
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const onMessage = (msg) => {
        const newMessage = JSON.parse(msg.body);
        setMessages([...messages, newMessage]);
    };

    const onTyping = (msg) => {
        // Handle typing notifications
        console.log('Typing notification:', JSON.parse(msg.body));
    };

    const onMessageSeen = (msg) => {
        // Handle message seen notifications
        console.log('Message seen notification:', JSON.parse(msg.body));
    };

    const sendMessage = () => {
        if (stompClient && stompClient.connected && inputMessage.trim() !== '') {
            const message = {
                from: 'admin', // Set the sender dynamically or fetch it from your authentication system
                message: inputMessage,
            };

            // Send the message to the server using Stomp
            stompClient.send('/app/chat/sendMessage', {}, JSON.stringify(message));

            // Clear the input field after sending the message
            setInputMessage('');
        }
    };

    return (
        <div>
            <h2>Chat</h2>
            <div>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.from}:</strong> {msg.message}
                    </div>
                ))}
            </div>
            <div>
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                />
                <button onClick={sendMessage} disabled={!stompClient || !stompClient.connected}>
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatComponent;
